import React from "react";
import styled from "styled-components";

const TitleContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const CardsContainer = styled.div`
  display: flex;
  gap: 35px;
  justify-content: center;
  flex-wrap: wrap;
  max-width: 900px;
  margin-left: auto;
  margin-right: auto;
`;

const Card = styled.div`
  height: 350px;
  width: 250px;
  background-color: green;
  border-radius: 40px;
  overflow: hidden;
  text-decoration: none;
  position: relative;
  display: block;
  .card:hover &nth-child(1) {
    transform: scale(10);
  }
`;

const CardImage = styled.img`
  width: 100%;
  height: auto;
`;

const CardOverlay = styled.div`
  background-color: white;
  height: 100px;
  width: 100%;
  position: absolute;
  bottom: 27%;
  left: 0;
  right: 0;
  z-index: 1;
  border-radius: calc(40px);
  transform: translateY(100%);
  transition: 0.2s ease-in-out;
`;

export default function LatestLessons() {
  return (
    <div>
      <TitleContainer>
        <h2>LATEST LESSONS</h2>
        <h4>Our latest lessons</h4>
      </TitleContainer>
      <CardsContainer>
        <Card>
          <CardImage></CardImage>
          <CardOverlay className='.cardOverlay'></CardOverlay>
        </Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
        <Card></Card>
      </CardsContainer>
    </div>
  );
}
