import React from "react";
import styled from "styled-components";
import videoBg from "./videos/videoBg.mp4";

const Button = styled.button`
  <!-- HTML !-->
<button class="button-40" role="button">Button 40</button>

/* CSS */
.button-40 {
    background-color: #111827;
    border: 1px solid transparent;
    border-radius: 0.75rem;
    box-sizing: border-box;
    color: #ffffff;
    cursor: pointer;
    flex: 0 0 auto;
    font-family: "Inter var", ui-sans-serif, system-ui, -apple-system, system-ui,
      "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif,
      "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol",
      "Noto Color Emoji";
    font-size: 1.125rem;
    font-weight: 600;
    line-height: 1.5rem;
    padding: 0.75rem 1.2rem;
    text-align: center;
    text-decoration: none #6b7280 solid;
    text-decoration-thickness: auto;
    transition-duration: 0.2s;
    transition-property: background-color, border-color, color, fill, stroke;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    user-select: none;
    -webkit-user-select: none;
    touch-action: manipulation;
    width: auto;
  }

  .button-40:hover {
    background-color: #374151;
  }

  .button-40:focus {
    box-shadow: none;
    outline: 2px solid transparent;
    outline-offset: 2px;
  }

  @media (min-width: 768px) {
    .button-40 {
      padding: 0.75rem 1.5rem;
    }
  }
`;

const HeroContainer = styled.div`
  text-align: center;
  max-height: 700px;
  display: flex;
  justify-items: center;
  'Montserrat', sans-serif;
  position: 'relative';
`;

export default function Hero() {
  return (
    <HeroContainer>
      <video
        src={videoBg}
        autoPlay
        muted
        loop
        style={{ width: "100%", height: "100%" }}
      />
      <div
        style={{
          color: "white",
          position: "absolute",
          // top: "10%",
          right: "15%",
          background: "rgba(0, 0, 0, 0.5)",
          padding: "2em",
        }}
      >
        <h1 style={{ fontSize: "44px", zIndex: 2 }}>
          ONLINE <br /> BRAZILIAN <br /> GUITAR <br /> LESSONS
        </h1>
        <div style={{ fontSize: "24px", fontWeight: "bold" }}>WITH ROCHO</div>
        <Button>Sign up</Button>
      </div>
    </HeroContainer>
  );
}
