import React, { useState } from "react";
import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";

const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  padding: 25px;
`;

const HeaderLinks = styled.div`
  display: flex;
  gap: 25px;
  @media (max-width: 768px) {
    display: none;
  }
`;

const HamburgerContainer = styled.div`
  display: none;
  @media (max-width: 768px) {
    display: flex;
    flex-direction: column;
  }
`;

const HeaderLinksMobile = styled.div`
  width: 300px;
  height: 100vh;
  display: none;
  flex-direction: column;
  transition: 1.5s;
  background-color: rgba(255, 255, 255, 0.5);
  position: sticky;
  transform: translateX(${(props) => props.positionX + "%"});
  @media (max-width: 768px) {
    display: flex;
  }
`;

export default function Header() {
  const [posX, setPosX] = useState(150);

  const element = (
    <FontAwesomeIcon
      icon={faBars}
      style={{ fontSize: "24px", width: "24px", alignSelf: "flex-end" }}
      onClick={() => (posX === 150 ? setPosX(-50) : setPosX(150))}
    />
  );
  return (
    <HeaderContainer>
      <div>Logo</div>
      <HeaderLinks>
        <div>courses</div>
        <div>book a lesson</div>
        <div>shop</div>
        <div>contact</div>
        <div>login</div>
      </HeaderLinks>
      <HamburgerContainer>
        {element}
        <HeaderLinksMobile positionX={posX}>
          <div>courses</div>
          <div>book a lesson</div>
          <div>shop</div>
          <div>contact</div>
          <div>login</div>
        </HeaderLinksMobile>
      </HamburgerContainer>
    </HeaderContainer>
  );
}
